import { useLoader } from "@react-three/fiber";
import { useGLTF } from "@react-three/drei";
import { useEffect, useRef } from "react";
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader'
import { OBJLoader } from 'three/examples/jsm/loaders/OBJLoader'
import { FBXLoader } from 'three/examples/jsm/loaders/FBXLoader'
import * as THREE from 'three'

export function DynamicModel({ url, type }) {
  const modelRef = useRef()

  const centerAndScaleObject = (object) => {
    // Create bounding box
    const box = new THREE.Box3().setFromObject(object)
    const center = box.getCenter(new THREE.Vector3())
    const size = box.getSize(new THREE.Vector3())

    // Get the maximum dimension
    const maxDim = Math.max(size.x, size.y, size.z)
    const scale = 5 / maxDim // Scale to fit in a 5 unit box

    // Center the object
    object.position.x = -center.x
    object.position.y = -center.y
    object.position.z = -center.z

    // Scale the object
    object.scale.multiplyScalar(scale)

    console.log('Model centered at:', center)
    console.log('Model scaled by:', scale)
  }

  // Handle GLTF/GLB files
  if (type === 'glb' || type === 'gltf') {
    const gltf = useGLTF(url)

    useEffect(() => {
      if (gltf.scene) {
        centerAndScaleObject(gltf.scene)
      }
    }, [gltf])

    return (
      <group ref={modelRef}>
        <primitive object={gltf.scene} />
      </group>
    )
  }

  // Handle FBX files
  if (type === 'fbx') {
    const fbx = useLoader(FBXLoader, url)

    useEffect(() => {
      if (fbx) {
        centerAndScaleObject(fbx)

        fbx.traverse((child) => {
          if (child.isMesh) {
            child.castShadow = true
            child.receiveShadow = true
            child.material.needsUpdate = true
          }
        })
      }
    }, [fbx])

    return (
      <group ref={modelRef}>
        <primitive object={fbx} />
      </group>
    )
  }

  // Handle OBJ files
  if (type === 'obj') {
    const obj = useLoader(OBJLoader, url)

    useEffect(() => {
      if (obj) {
        centerAndScaleObject(obj)
      }
    }, [obj])

    return (
      <group ref={modelRef}>
        <primitive object={obj} />
      </group>
    )
  }

  return null
}
