import { OrbitControls } from "@react-three/drei";
import { Suspense } from "react";
import { DynamicModel } from "./DynamicModel";
import { AsciiRenderer } from "./AsciiRenderer";
import Model from "../Model";

export function Scene({ modelUrl, modelType, theme, themes }) {
  return (
    <>
      <color attach="background" args={[themes[theme].background]} />

      <ambientLight intensity={0.5} />
      <directionalLight 
        position={[0, 5, 5]} 
        intensity={1} 
        castShadow
      />
      <directionalLight 
        position={[0, -5, -5]} 
        intensity={0.5} 
      />

      <Suspense fallback={null}>
        {modelUrl ? (
          <DynamicModel url={modelUrl} type={modelType} />
        ) : (
          <Model />
        )}
        <OrbitControls 
          enablePan={true}
          enableZoom={true}
          enableRotate={true}
          minDistance={2}
          maxDistance={20}
          target={[0, 0, 0]}
        />
        <AsciiRenderer 
          fgColor={themes[theme].fgColor}
          bgColor={themes[theme].bgColor}
          resolution={0.25}
          characters=' .:-=+*#%@'
          invert={theme === 'dark'}
          color={false}
        />
      </Suspense> 
    </>
  )
}
