import { OrbitControls, PerspectiveCamera } from "@react-three/drei";
import { useThree } from "@react-three/fiber";
import { useEffect } from "react";
import { DynamicModel } from "./DynamicModel";
import { AsciiRenderer } from "./AsciiRenderer";
import Model from "../Model";

export function Scene({ modelUrl, modelType, theme, themes }) {
  const { camera } = useThree();

  // Set initial camera position and target
  useEffect(() => {
    if (camera) {
      camera.position.set(0, 0, 15);
      camera.updateProjectionMatrix();
    }
  }, [camera]);

  return (
    <>
      <color attach="background" args={[themes[theme].background]} />

      {/* Center group with lights */}
      <group position={[0, 0, 0]}>
        <ambientLight intensity={0.5} />
        <directionalLight 
          position={[5, 5, 5]} 
          intensity={1} 
          castShadow
        />
        <directionalLight 
          position={[-5, -5, -5]} 
          intensity={0.5} 
        />

        {/* Debug helpers */}
        <axesHelper args={[5]} /> {/* Shows X,Y,Z axes */}
        <gridHelper args={[10, 10]} /> {/* Shows ground grid */}

        {/* Center point helper */}
        <mesh position={[0, 0, 0]} scale={0.2}>
          <sphereGeometry />
          <meshBasicMaterial color="red" />
        </mesh>

        {/* Centered model */}
        <group position={[0, 0, 0]} rotation={[0, 0, 0]}>
          {modelUrl ? (
            <DynamicModel url={modelUrl} type={modelType} />
          ) : (
            <Model />
          )}
        </group>
      </group>

      <OrbitControls 
        makeDefault
        enablePan={true}
        enableZoom={true}
        enableRotate={true}
        minDistance={2}
        maxDistance={20}
        target={[0, 0, 0]}
        camera={camera}
      />

      <AsciiRenderer 
        fgColor={themes[theme].fgColor}
        bgColor={themes[theme].bgColor}
        resolution={0.25}
        characters=' .:-=+*#%@'
        invert={theme === 'dark'}
        color={false}
      />
    </>
  )
}
