import "./styles.css";
import { Canvas, useLoader } from "@react-three/fiber";
import { Environment, OrbitControls, useGLTF } from "@react-three/drei";
import { Suspense } from "react";
import Model from "./Model";

import { useEffect, useRef, useState, useMemo, useLayoutEffect } from 'react'
import { useFrame, useThree } from '@react-three/fiber'
import { useCursor, Html, useProgress} from  '@react-three/drei'
import { AsciiEffect } from 'three-stdlib'
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader'
import { OBJLoader } from 'three/examples/jsm/loaders/OBJLoader'
import { FBXLoader } from 'three/examples/jsm/loaders/FBXLoader'

export default function App() {
  const [modelUrl, setModelUrl] = useState(null)
  const [modelType, setModelType] = useState(null)
  const [theme, setTheme] = useState('dark')

  const themes = {
    dark: {
      background: 'black',
      fgColor: 'white',
      bgColor: 'black'
    },
    light: {
      background: 'white',
      fgColor: 'black',
      bgColor: 'white'
    }
  }

  const handleFileUpload = (event) => {
    const file = event.target.files[0]
    if (file) {
      const url = URL.createObjectURL(file)
      const type = file.name.split('.').pop().toLowerCase()
      console.log('File uploaded:', { 
        name: file.name, 
        type: type, 
        url: url 
      })
      setModelUrl(url)
      setModelType(type)
    }
  }

  function DynamicModel({ url, type }) {
    const modelRef = useRef()

    // Handle GLTF/GLB files
    if (type === 'glb' || type === 'gltf') {
      const gltf = useGLTF(url)
      return (
        <group ref={modelRef} dispose={null}>
          <primitive 
            object={gltf.scene} 
            position={[0, 0, 0]}
            scale={[2, 2, 2]}
          />
        </group>
      )
    }

    // Handle FBX files
    if (type === 'fbx') {
      const fbx = useLoader(FBXLoader, url)

      useEffect(() => {
        if (fbx) {
          // Center the model
          fbx.position.set(0, 0, 0)

          // Scale down FBX (they tend to be much larger)
          fbx.scale.set(0.01, 0.01, 0.01)

          // Optional: rotate if needed
          fbx.rotation.set(0, Math.PI, 0)

          // Traverse and setup materials if needed
          fbx.traverse((child) => {
            if (child.isMesh) {
              child.castShadow = true
              child.receiveShadow = true
            }
          })
        }
      }, [fbx])

      return <primitive object={fbx} ref={modelRef} />
    }

    // Handle OBJ files
    if (type === 'obj') {
      const obj = useLoader(OBJLoader, url)
      return <primitive object={obj} ref={modelRef} />
    }

    return null
  }

  function AsciiRenderer({
    renderIndex = 1,
    bgColor = 'black',
    fgColor = 'white',  
    characters = ' .:-=+*#%@',
    invert = true,
    color = false,
    resolution = 0.2
  }) {
    // Reactive state
    const { size, gl, scene, camera } = useThree()
    const aspectRatio = size.width / size.height

    // Create effect
    const effect = useMemo(() => {
      const effect = new AsciiEffect(gl, characters, { invert, color, resolution })
      effect.domElement.style.position = 'absolute'
      effect.domElement.style.top = '0px'
      effect.domElement.style.left = '0px'
      effect.domElement.style.pointerEvents = 'none'
      //effect.setSize(size.width, size.height / aspectRatio)
      return effect
    }, [characters, invert, color, aspectRatio, resolution])

    // Styling
    useLayoutEffect(() => {
      effect.domElement.style.color = fgColor
      effect.domElement.style.backgroundColor = bgColor
    }, [fgColor, bgColor])

    // Append on mount, remove on unmount
    useEffect(() => {
      gl.domElement.style.opacity = '0'
      gl.domElement.parentNode.appendChild(effect.domElement)
      return () => {
        gl.domElement.style.opacity = '1'
        gl.domElement.parentNode.removeChild(effect.domElement)
      }
    }, [effect])

    // Set size
    useEffect(() => {
      effect.setSize(size.width, size.height)
    }, [effect, size])

    // Take over render-loop (that is what the index is for)
    useFrame((state) => {
      effect.render(scene, camera)
    }, renderIndex)

    // This component returns nothing, it is a purely logical
  }

  return (
    <div style={{ width: '100%', height: '100vh' }}>
      <div className="controls">
        <input
          className="file-input"
          type="file"
          accept=".glb,.gltf,.fbx,.obj"
          onChange={handleFileUpload}
        />
        <button 
          className={`theme-btn ${theme === 'dark' ? 'active' : ''}`}
          onClick={() => setTheme('dark')}
        >
          Dark Mode
        </button>
        <button 
          className={`theme-btn ${theme === 'light' ? 'active' : ''}`}
          onClick={() => setTheme('light')}
        >
          Light Mode
        </button>
      </div>

      <Canvas 
        camera={{ 
          position: [5, 5, 5],
          fov: 60,
          near: 0.1,
          far: 1000
        }}
      >
        <color attach="background" args={[themes[theme].background]} />
        <ambientLight intensity={0.5} />
        <directionalLight 
          position={[10, 10, 5]} 
          intensity={1} 
          castShadow
        />
        <directionalLight 
          position={[-10, -10, -5]} 
          intensity={0.5} 
        />
        <hemisphereLight 
          intensity={0.3}
          groundColor="black"
        />

        <Suspense fallback={null}>
          {modelUrl ? (
            <DynamicModel url={modelUrl} type={modelType} />
          ) : (
            <Model />
          )}
          <OrbitControls 
            enablePan={true}
            enableZoom={true}
            enableRotate={true}
            minDistance={2}
            maxDistance={20}
          />
          <AsciiRenderer 
            fgColor={themes[theme].fgColor}
            bgColor={themes[theme].bgColor}
            resolution={0.15}
          />
        </Suspense> 
      </Canvas>
    </div>
  );
}
