import { OrbitControls, PerspectiveCamera } from "@react-three/drei";
import { useThree } from "@react-three/fiber";
import { useEffect } from "react";
import { DynamicModel } from "./DynamicModel";
import { AsciiRenderer } from "./AsciiRenderer";
import Model from "../Model";

export function Scene({ modelUrl, modelType, theme, themes }) {
  const { camera } = useThree();

  return (
    <group>
      <color attach="background" args={['black']} />

      <group position={[0, 0, 0]}>
        <ambientLight intensity={0.5} />
        <directionalLight 
          position={[5, 5, 5]} 
          intensity={1} 
          castShadow
        />
        <directionalLight 
          position={[-5, -5, -5]} 
          intensity={0.5} 
        />

        {/* Detailed cube */}
        <mesh position={[0, 0, 0]} scale={1}>
          <boxGeometry args={[1, 1, 1]} />
        </mesh>
      </group>

      <OrbitControls 
        makeDefault
        enablePan={true}
        enableZoom={true}
        enableRotate={true}
        minDistance={2}
        maxDistance={20}
        target={[0, 0, 0]}
        camera={camera}
      />

      <AsciiRenderer 
        fgColor="white"
        bgColor="black"
        resolution={0.15}
        characters='@%#*+=-:. '
        invert={true}
        color={false}
      />
    </group>
  )
}
