import "./styles.css";
import { Canvas, useLoader } from "@react-three/fiber";
import { Environment, OrbitControls, useGLTF } from "@react-three/drei";
import { Suspense, useEffect, useRef, useState } from "react";
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader'
import { OBJLoader } from 'three/examples/jsm/loaders/OBJLoader'
import { FBXLoader } from 'three/examples/jsm/loaders/FBXLoader'
import * as THREE from 'three'
import { AsciiRenderer } from './components/AsciiRenderer'
import Model from "./Model";

export default function App() {
  const [modelUrl, setModelUrl] = useState(null)
  const [modelType, setModelType] = useState(null)
  const [theme, setTheme] = useState('dark')

  const themes = {
    dark: {
      background: 'black',
      fgColor: 'white',
      bgColor: 'black'
    },
    light: {
      background: 'white',
      fgColor: 'black',
      bgColor: 'white'
    }
  }

  const handleFileUpload = (event) => {
    const file = event.target.files[0]
    if (file) {
      const url = URL.createObjectURL(file)
      const type = file.name.split('.').pop().toLowerCase()
      console.log('File uploaded:', { 
        name: file.name, 
        type: type, 
        url: url 
      })
      setModelUrl(url)
      setModelType(type)
    }
  }

  function DynamicModel({ url, type }) {
    const modelRef = useRef()

    // Handle GLTF/GLB files
    if (type === 'glb' || type === 'gltf') {
      const gltf = useGLTF(url)
      return (
        <group ref={modelRef} dispose={null}>
          <primitive 
            object={gltf.scene} 
            position={[0, 0, 0]}
            scale={[2, 2, 2]}
          />
        </group>
      )
    }

    // Handle FBX files
    if (type === 'fbx') {
      const fbx = useLoader(FBXLoader, url)

      useEffect(() => {
        if (fbx) {
          // Reset position
          fbx.position.set(0, -2, 0)

          // Try a much smaller scale
          fbx.scale.set(0.001, 0.001, 0.001)

          // Setup materials
          fbx.traverse((child) => {
            if (child.isMesh) {
              child.castShadow = true
              child.receiveShadow = true
              child.material.needsUpdate = true
            }
          })

          // Create bounding box helper for debugging
          const box = new THREE.Box3().setFromObject(fbx)
          const size = new THREE.Vector3()
          box.getSize(size)
          console.log('Model size:', size)
          console.log('Model position:', fbx.position)
        }
      }, [fbx])

      return (
        <group ref={modelRef}>
          <primitive 
            object={fbx} 
            dispose={null}
          />
        </group>
      )
    }

    // Handle OBJ files
    if (type === 'obj') {
      const obj = useLoader(OBJLoader, url)
      return <primitive object={obj} ref={modelRef} />
    }

    return null
  }

  return (
    <div style={{ width: '100%', height: '100vh' }}>
      <div className="controls">
        <input
          className="file-input"
          type="file"
          accept=".glb,.gltf,.fbx,.obj"
          onChange={handleFileUpload}
        />
        <button 
          className={`theme-btn ${theme === 'dark' ? 'active' : ''}`}
          onClick={() => setTheme('dark')}
        >
          Dark Mode
        </button>
        <button 
          className={`theme-btn ${theme === 'light' ? 'active' : ''}`}
          onClick={() => setTheme('light')}
        >
          Light Mode
        </button>
      </div>

      <Canvas 
        camera={{ 
          position: [0, 2, 5],
          fov: 60,
          near: 0.1,
          far: 1000,
          aspect: window.innerWidth / window.innerHeight
        }}
        gl={{ 
          preserveDrawingBuffer: true,
          antialias: true
        }}
      >
        <color attach="background" args={[themes[theme].background]} />

        <ambientLight intensity={0.5} />
        <directionalLight 
          position={[0, 5, 5]} 
          intensity={1} 
          castShadow
        />
        <directionalLight 
          position={[0, -5, -5]} 
          intensity={0.5} 
        />

        <Suspense fallback={null}>
          {modelUrl ? (
            <DynamicModel url={modelUrl} type={modelType} />
          ) : (
            <Model />
          )}
          <OrbitControls 
            enablePan={true}
            enableZoom={true}
            enableRotate={true}
            minDistance={2}
            maxDistance={20}
            target={[0, 0, 0]}
          />
          <AsciiRenderer 
            fgColor={themes[theme].fgColor}
            bgColor={themes[theme].bgColor}
            resolution={0.25}
            characters=' .:-=+*#%@'
            invert={theme === 'dark'}
            color={false}
          />
        </Suspense> 
      </Canvas>
    </div>
  );
}
