import { useEffect, useMemo } from 'react'
import { useFrame, useThree } from '@react-three/fiber'
import { AsciiEffect } from 'three-stdlib'
import * as THREE from 'three'

export function AsciiRenderer({
  renderIndex = 1,
  bgColor = '#000000',
  fgColor = '#ffffff',
  characters = '@%#*+=-:. ',
  invert = true,
  color = false,
  resolution = 0.15
}) {
  const { gl, scene, camera, size } = useThree()

  // Create ASCII effect
  const effect = useMemo(() => {
    const asciiEffect = new AsciiEffect(gl, characters, {
      invert,
      color,
      resolution
    })

    // Set initial size
    asciiEffect.setSize(size.width, size.height)

    // Style the ASCII canvas
    asciiEffect.domElement.style.position = 'absolute'
    asciiEffect.domElement.style.top = '0'
    asciiEffect.domElement.style.left = '0'
    asciiEffect.domElement.style.width = '100%'
    asciiEffect.domElement.style.height = '100%'
    asciiEffect.domElement.style.backgroundColor = bgColor
    asciiEffect.domElement.style.color = fgColor
    asciiEffect.domElement.style.fontFamily = 'monospace'
    asciiEffect.domElement.style.whiteSpace = 'pre'
    asciiEffect.domElement.style.margin = '0'
    asciiEffect.domElement.style.fontSize = '16px'
    asciiEffect.domElement.style.lineHeight = '0.8'

    return asciiEffect
  }, [gl, characters, invert, color, resolution, bgColor, fgColor, size])

  // Handle mounting/unmounting
  useEffect(() => {
    const parent = gl.domElement.parentElement
    if (parent) {
      parent.appendChild(effect.domElement)
      gl.domElement.style.display = 'none'
    }

    return () => {
      if (parent) {
        parent.removeChild(effect.domElement)
        gl.domElement.style.display = 'block'
      }
    }
  }, [gl, effect])

  // Handle resizing
  useEffect(() => {
    const handleResize = () => {
      effect.setSize(size.width, size.height)
      gl.setSize(size.width, size.height)

      if (camera instanceof THREE.PerspectiveCamera) {
        camera.aspect = size.width / size.height
        camera.updateProjectionMatrix()
      }
    }

    window.addEventListener('resize', handleResize)
    handleResize()

    return () => window.removeEventListener('resize', handleResize)
  }, [gl, effect, camera, size])

  // Render loop
  useFrame(() => {
    effect.render(scene, camera)
  }, renderIndex)

  return null
}
