import { useEffect, useMemo, useLayoutEffect } from 'react'
import { useFrame, useThree } from '@react-three/fiber'
import { AsciiEffect } from 'three-stdlib'

export function AsciiRenderer({
  renderIndex = 1,
  bgColor = 'black',
  fgColor = 'white',  
  characters = ' .:-=+*#%@',
  invert = true,
  color = false,
  resolution = 0.55
}) {
  const { size, gl, scene, camera } = useThree()

  const effect = useMemo(() => {
    const effect = new AsciiEffect(gl, characters, { 
      invert, 
      color, 
      resolution,
      width: size.width,
      height: size.height
    })

    // Debug element positioning
    effect.domElement.style.position = 'absolute'
    effect.domElement.style.top = '0px'
    effect.domElement.style.left = '0px'
    effect.domElement.style.width = '100%'
    effect.domElement.style.height = '100%'
    effect.domElement.style.pointerEvents = 'none'

    // Add debug info
    console.log('ASCII Effect created with dimensions:', {
      width: size.width,
      height: size.height
    })

    return effect
  }, [gl, characters, invert, color, resolution, size.width, size.height])

  useEffect(() => {
    const parent = gl.domElement.parentNode
    if (parent) {
      // Debug parent element
      console.log('Parent element dimensions:', {
        width: parent.clientWidth,
        height: parent.clientHeight,
        offsetLeft: parent.offsetLeft,
        offsetTop: parent.offsetTop,
        center: {
          x: parent.clientWidth / 2,
          y: parent.clientHeight / 2
        }
      })

      gl.domElement.style.opacity = '0'
      parent.appendChild(effect.domElement)
    }
  }, [gl, effect])

  useFrame(() => {
    // Debug camera and scene position
    console.log('Camera position:', camera.position)
    console.log('Scene center:', {
      x: scene.position.x,
      y: scene.position.y,
      z: scene.position.z
    })

    effect.render(scene, camera)
  }, renderIndex)

  return null
}
