import { useEffect, useMemo, useLayoutEffect } from 'react'
import { useFrame, useThree } from '@react-three/fiber'
import { AsciiEffect } from 'three-stdlib'

export function AsciiRenderer({
  renderIndex = 1,
  bgColor = 'black',
  fgColor = 'white',  
  characters = ' .:-=+*#%@',
  invert = true,
  color = false,
  resolution = 0.55
}) {
  const { size, gl, scene, camera } = useThree()

  const effect = useMemo(() => {
    // Get the actual canvas dimensions
    const canvas = gl.domElement
    const width = canvas.clientWidth
    const height = canvas.clientHeight

    console.log('Canvas dimensions:', { width, height })
    console.log('GL viewport:', gl.getViewport())

    const effect = new AsciiEffect(gl, characters, { 
      invert, 
      color, 
      resolution
    })

    // Set size explicitly to match canvas
    effect.setSize(width, height)

    effect.domElement.style.cssText = `
      position: absolute;
      top: 0;
      left: 0;
      width: ${width}px;  // Use explicit pixel values
      height: ${height}px;
      background-color: ${bgColor};
      color: ${fgColor};
      font-family: monospace;
      white-space: pre;
      overflow: hidden;
    `.trim()

    return effect
  }, [gl, characters, invert, color, resolution, bgColor, fgColor])

  useEffect(() => {
    const parent = gl.domElement.parentNode
    if (parent) {
      // Match parent dimensions
      const width = parent.clientWidth
      const height = parent.clientHeight

      effect.setSize(width, height)
      gl.setSize(width, height)

      gl.domElement.style.opacity = '0'
      parent.appendChild(effect.domElement)
    }
    return () => {
      if (parent) {
        gl.domElement.style.opacity = '1'
        parent.removeChild(effect.domElement)
      }
    }
  }, [gl, effect])

  // Handle resize
  useEffect(() => {
    const handleResize = () => {
      const parent = gl.domElement.parentNode
      if (parent) {
        const width = parent.clientWidth
        const height = parent.clientHeight
        effect.setSize(width, height)
        gl.setSize(width, height)
      }
    }

    window.addEventListener('resize', handleResize)
    return () => window.removeEventListener('resize', handleResize)
  }, [gl, effect])

  useFrame(() => {
    effect.render(scene, camera)
  }, renderIndex)

  return null
}
