import { useEffect, useMemo, useLayoutEffect } from 'react'
import { useFrame, useThree } from '@react-three/fiber'
import { AsciiEffect } from 'three-stdlib'
import * as THREE from 'three'
export function AsciiRenderer({
  renderIndex = 1,
  bgColor = 'black',
  fgColor = 'white',  
  characters = ' .:-=+*#%@',
  invert = true,
  color = false,
  resolution = 0.55
}) {
  const { gl, scene, camera } = useThree()

  const effect = useMemo(() => {
    const effect = new AsciiEffect(gl, characters, { 
      invert, 
      color, 
      resolution
    })

    const container = gl.domElement.parentNode
    const width = container.clientWidth
    const height = container.clientHeight

    effect.setSize(width, height)
    gl.setSize(width, height, false)

    effect.domElement.style.cssText = `
      position: absolute;
      left: 50%;
      top: 50%;
      transform: translate(-50%, -50%);
      background-color: ${bgColor};
      color: ${fgColor};
      font-family: monospace;
      width: 100%;
      height: 100%;
    `.trim()

    container.appendChild(effect.domElement)

    return effect
  }, [gl, characters, invert, color, resolution, bgColor, fgColor])

  useEffect(() => {
    return () => {
      const container = gl.domElement.parentNode
      if (container && effect.domElement) {
        container.removeChild(effect.domElement)
      }
    }
  }, [gl, effect])

  useEffect(() => {
    function handleResize() {
      const container = gl.domElement.parentNode
      const width = container.clientWidth
      const height = container.clientHeight

      effect.setSize(width, height)
      gl.setSize(width, height, false)

      if (camera instanceof THREE.PerspectiveCamera) {
        camera.aspect = width / height
        camera.updateProjectionMatrix()
      }
    }

    window.addEventListener('resize', handleResize)
    handleResize()

    return () => window.removeEventListener('resize', handleResize)
  }, [gl, effect, camera])

  useFrame(() => {
    effect.render(scene, camera)
  }, renderIndex)

  return null
}
