import "./styles.css";
import { Canvas, useLoader } from "@react-three/fiber";
import { Environment, OrbitControls } from "@react-three/drei";
import { Suspense } from "react";
import Model from "./Model";

import { useEffect, useRef, useState, useMemo, useLayoutEffect } from 'react'
import { useFrame, useThree } from '@react-three/fiber'
import { useCursor, Html, useProgress} from  '@react-three/drei'
import { AsciiEffect } from 'three-stdlib'
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader'
import { OBJLoader } from 'three/examples/jsm/loaders/OBJLoader'
import { FBXLoader } from 'three/examples/jsm/loaders/FBXLoader'

export default function App() {
  const [modelUrl, setModelUrl] = useState(null)
  const [modelType, setModelType] = useState(null)

  const handleFileUpload = (event) => {
    const file = event.target.files[0]
    if (file) {
      const url = URL.createObjectURL(file)
      setModelUrl(url)
      setModelType(file.name.split('.').pop().toLowerCase())
    }
  }

  function DynamicModel({ url, type }) {
    const modelRef = useRef()

    console.log('Loading model:', { url, type })

    const loader = useLoader(
      type === 'glb' || type === 'gltf' ? GLTFLoader :
      type === 'fbx' ? FBXLoader :
      type === 'obj' ? OBJLoader : null,
      url
    )

    useEffect(() => {
      if (loader) {
        console.log('Model loaded:', loader)
        const model = type === 'glb' || type === 'gltf' ? loader.scene : loader

        model.position.set(0, 0, 0)

        model.scale.set(2, 2, 2)

        if (modelRef.current) {
          modelRef.current.position.set(0, 0, 0)
        }
      }
    }, [loader, type])

    if (!loader) {
      console.error('No loader available for type:', type)
      return null
    }

    return (
      <group ref={modelRef}>
        {type === 'glb' || type === 'gltf' ? (
          <primitive object={loader.scene} />
        ) : (
          <primitive object={loader} />
        )}
      </group>
    )
  }

  function AsciiRenderer({
    renderIndex = 1,
    bgColor = 'black',
    fgColor = 'white',  
    characters = ' .:-=+*#%@',
    invert = true,
    color = false,
    resolution = 0.2
  }) {
    // Reactive state
    const { size, gl, scene, camera } = useThree()
    const aspectRatio = size.width / size.height

    // Create effect
    const effect = useMemo(() => {
      const effect = new AsciiEffect(gl, characters, { invert, color, resolution })
      effect.domElement.style.position = 'absolute'
      effect.domElement.style.top = '0px'
      effect.domElement.style.left = '0px'
      effect.domElement.style.pointerEvents = 'none'
      //effect.setSize(size.width, size.height / aspectRatio)
      return effect
    }, [characters, invert, color, aspectRatio, resolution])

    // Styling
    useLayoutEffect(() => {
      effect.domElement.style.color = fgColor
      effect.domElement.style.backgroundColor = bgColor
    }, [fgColor, bgColor])

    // Append on mount, remove on unmount
    useEffect(() => {
      gl.domElement.style.opacity = '0'
      gl.domElement.parentNode.appendChild(effect.domElement)
      return () => {
        gl.domElement.style.opacity = '1'
        gl.domElement.parentNode.removeChild(effect.domElement)
      }
    }, [effect])

    // Set size
    useEffect(() => {
      effect.setSize(size.width, size.height)
    }, [effect, size])

    // Take over render-loop (that is what the index is for)
    useFrame((state) => {
      effect.render(scene, camera)
    }, renderIndex)

    // This component returns nothing, it is a purely logical
  }

  return (
    <div style={{ width: '100%', height: '100vh' }}>
      <input
        className="file-input"
        type="file"
        accept=".glb,.gltf,.fbx,.obj"
        onChange={handleFileUpload}
      />

      <Canvas 
        camera={{ 
          position: [0, 2, 5],
          fov: 75,
          near: 0.1,
          far: 1000
        }}
      >
        <color attach="background" args={['black']} />
        <ambientLight intensity={1} />
        <spotLight position={[10, 10, 10]} angle={0.15} penumbra={1} intensity={1} />
        <pointLight position={[-10, -10, -10]} intensity={1} />
        <Suspense fallback={null}>
          {modelUrl ? (
            <DynamicModel url={modelUrl} type={modelType} />
          ) : (
            <Model />
          )}
          <OrbitControls 
            enablePan={true}
            enableZoom={true}
            enableRotate={true}
          />
          <AsciiRenderer fgColor="white" bgColor="black" />
        </Suspense> 
      </Canvas>
    </div>
  );
}
