import { OrbitControls } from "@react-three/drei";
import { Suspense } from "react";
import { DynamicModel } from "./DynamicModel";
import { AsciiRenderer } from "./AsciiRenderer";
import Model from "../Model";

export function Scene({ modelUrl, modelType, theme, themes }) {
  return (
    <>
      <color attach="background" args={[themes[theme].background]} />

      {/* Centered lights */}
      <ambientLight intensity={0.5} />
      <directionalLight 
        position={[10, 10, 10]} 
        intensity={1} 
      />
      <directionalLight 
        position={[-10, -10, -10]} 
        intensity={0.5} 
      />

      <Suspense fallback={null}>
        {/* Center point helper (for debugging) */}
        <mesh position={[0, 0, 0]} scale={0.1}>
          <boxGeometry />
          <meshBasicMaterial color="red" />
        </mesh>

        {/* Centered model group */}
        <group position={[0, 0, 0]}>
          {modelUrl ? (
            <DynamicModel url={modelUrl} type={modelType} />
          ) : (
            <Model />
          )}
        </group>

        <OrbitControls 
          makeDefault
          enablePan={true}
          enableZoom={true}
          enableRotate={true}
          minDistance={2}
          maxDistance={20}
          target={[0, 0, 0]}  // Explicitly set target to center
        />

        <AsciiRenderer 
          fgColor={themes[theme].fgColor}
          bgColor={themes[theme].bgColor}
          resolution={0.25}
          characters=' .:-=+*#%@'
          invert={theme === 'dark'}
          color={false}
        />
      </Suspense>
    </>
  )
}
